import { useState } from "react";
import Content from "../content/Content";

function NavBar() {
    const [content, setContent] = useState("Home");

    return (
        <>
            <ul className="nav justify-content-end mt-3">
                <li className="nav-item">
                    <a href="#" className="nav-link mynavlink" onClick={() => setContent("Home")}>Home</a>
                </li>
                <li className="nav-item">
                    <a href="#" className="nav-link mynavlink" onClick={() => setContent("Sobre")}>Sobre</a>
                </li>
                <li className="nav-item">
                    <a href="#" className="nav-link mynavlink" onClick={() => setContent("Lista de Livros")}>Lista de Livros</a>
                </li>
                <li className="nav-item">
                    <a href="#" className="nav-link mynavlink" onClick={() => setContent("Cadastrar")}>Cadastrar</a>
                </li>
            </ul>

            {/* Renderiza o conteúdo baseado no estado */}
            <Content page={content} />
        </>
    );
}
export default NavBar;
