function BookForm(props) {

    return (
        <div className="container mt-4">
        <h2>Cadastrar Livros Lidos</h2>
        <p>Utilize o formulário abaixo para cadastrar novos livros na lista:</p>

        <h3>Lista de livros lidos até o momento:</h3>
            <ol className="list-group list-group-numbered">
                {listaLivros.map((livro) => (
                    <li key={livro.id} className="list-group-item">
                        <strong>{livro.titulo}</strong> - {livro.autor} | <em>Gênero:</em> {livro.genero} | <em>Lido em:</em> {livro.data}
                    </li>
                ))}
            </ol>
        </div>
    );
}

export default BookForm;
